<?phpisset($_GET['action']) ? $action = $_GET['action'] : $action = 'list';isset($_GET['section']) ? $section = $_GET['section'] : $section = 'list';isset($_GET['id']) ? $id = $_GET['id'] : $id = 0;switch ($action):case'list':        $QueryObj = new query('navigations');        $QueryObj->DisplayAll();        break;    case'insert':        if (isset($_POST['submit'])):            $QueryObj = new query('navigations');            $not = array('submit', 'is_active');            $QueryObj->Data = MakeDataArray($_POST, $not);            $QueryObj->Data['is_active'] = isset($_POST['is_active']) ? 1 : 0;            $QueryObj->Insert();            $admin_user->set_pass_msg('The navigation has been added.');            Redirect(make_admin_url('navigation', 'list', 'list', 'id=' . $_POST['id']));        endif;        break;    case'update':        $QueryObj = new query('navigations');        $QueryObj->Where = "where id='" . $id . "'";        $navigations = $QueryObj->DisplayOne();                if (isset($_POST['submit'])):            $QueryObj = new query('navigations');                        $not = array('submit', 'is_active');            $QueryObj->Data = MakeDataArray($_POST, $not);            $QueryObj->Data['id'] = $id;            $QueryObj->Data['is_active'] = isset($_POST['is_active']) ? 1 : 0;            $QueryObj->Update();            $admin_user->set_pass_msg('The navigation has been updated');            Redirect(make_admin_url('navigation', 'list', 'list', 'id=' . $navigations->id));        endif;        break;    case 'update2':        if (isset($_POST['submit'])):                          $query= new query('navigations');                    $query->Data['is_active']='0';                    $query->UpdateCustom();                                        foreach ($_POST['is_active'] as $k=>$v):                            $q= new query('navigations');                            $q->Data['is_active']=1;                            $q->Data['id']=$k;                            $q->Update();                    endforeach;                endif;         $admin_user->set_pass_msg('Operation has been performed successfully');        Redirect(make_admin_url('navigation', 'list', 'list', 'id='.$id));        break;    case'delete':        $QueryObj = new query('navigations');        $QueryObj->id = $id;        $QueryObj->Delete();        Redirect(make_admin_url('navigation', 'list', 'list', 'id=' . $id));        break;    default:break;endswitch;?>